import React, { useState } from 'react';
import { Formik, useField, FieldArray, Form } from 'formik';
import 'https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/5.3.0/css/bootstrap.min.css';

const INITIAL_TODOS = [
  { id: 1, content: "learn react", isCompleted: true },
  { id: 2, content: "learn react hooks", isCompleted: true },
  { id: 3, content: "learn formik", isCompleted: false }
];

const TodoItem = ({ index, isEditing, setEditing }) => {
  const [completedField, , completedHelpers] = useField({ name: `todos[${index}].isCompleted`, type: "checkbox" });
  const [contentField, contentMeta, contentHelpers] = useField(`todos[${index}].content`);

  const onContentInput = e => {
    contentHelpers.setValue(e.currentTarget.innerText);
  };

  const onContentBlur = () => {
    contentHelpers.setTouched(true);
  };

  return (
    <div className="form-check">
      <input
        className="form-check-input"
        type="checkbox"
        name={completedField.name}
        checked={completedField.value}
        onChange={({ target }) => {
          completedHelpers.setValue(target.checked);
          completedHelpers.setTouched(true);
        }}
      />
      {isEditing === index ? (
        <input
          className="form-control"
          value={contentField.value}
          onChange={e => contentHelpers.setValue(e.target.value)}
        />
      ) : (
        <label
          className={`form-check-label ${completedField.value ? "completed" : ""}`}
          contentEditable={true}
          onInput={onContentInput}
          onBlur={onContentBlur}
        >
          {contentField.value}
        </label>
      )}
      <button
        className="btn btn-primary"
        onClick={() => setEditing(isEditing !== index ? index : null)}
      >
        {isEditing === index ? 'Save' : 'Edit'}
      </button>
    </div>
  );
};

export const TodoApp = () => {
  const [isEditing, setEditing] = useState(null);

  return (
    <Formik
      initialValues={{ todos: INITIAL_TODOS }}
      onSubmit={values => {
        console.log(values);
      }}
    >
      <Form>
        <FieldArray name="todos">
          {({ remove, ...fieldArrayHelpers }) => {
            const onAddClick = () => {
              fieldArrayHelpers.push({
                id: fieldArrayHelpers.form.values.todos.length + 1,
                content: "",
                isCompleted: false
              });
            };

            return (
              <React.Fragment>
                <button className="btn btn-primary mb-3" onClick={onAddClick}>
                  Add Item
                </button>
                {fieldArrayHelpers.form.values.todos.map((todo, index) => (
                  <div key={todo.id}>
                    <TodoItem
                      index={index}
                      isEditing={isEditing}
                      setEditing={setEditing}
                    />
                    <button
                      className="btn btn-danger"
                      onClick={() => remove(index)}
                    >
                      Delete
                    </button>
                  </div>
                ))}
                <button className="btn btn-primary mt-3" type="submit">
                  Submit
                </button>
              </React.Fragment>
            );
          }}
        </FieldArray>
      </Form>
    </Formik>
  );
};

export default App;
